from check_power2 import check_power2
import unittest

checks = 0
class Test(unittest.TestCase):
	def assert_equals(a,b,checks=checks):
		if a != b:
			print(f"{a} \n should equal \n{b}")
			print(f"test {checks}")
		assert a == b
		checks += 1

		
